mapper.update:
update tb_signet_book
user_id = #{userId}, title = #{title}, book_type = #{bookType}, intro = #{intro}, image = #{image}, music = #{music}, foot_prints = #{footPrints}, modify_time = #{modifyTime}, create_time = #{createTime},
where id = #{id}

mapper.sql:
		from
			tb_signet_book

			 and id = #{id} 
			 and user_id = #{userId} 
			 and title = #{title} 
			 and book_type = #{bookType} 
			 and intro = #{intro} 
			 and image = #{image} 
			 and music = #{music} 
			 foot_prints = #{footPrints} 
			 and modify_time = #{modifyTime} 
			 and create_time = #{createTime} 
		 and unix_timestamp(create_time) * 1000 > # {ta} 
         and unix_timestamp(create_time) * 1000 < # {tb} 

			and id in
			(-1)


				#{item}
